/* eslint-disable no-restricted-globals */
import React, { useState, useEffect } from 'react'
import ReactDOM from 'react-dom'
import {
    BrowserRouter as Router,
    Switch, Route, Link, useRouteMatch, useHistory,
  } from "react-router-dom"


 export const useField = (type) => {
    const [value, setValue] = useState('')

    const onChange = (event) => {
      setValue(event.target.value)
    }

    const reset = () => {
      setValue('')
    }

    console.log('reset', reset)

    return { 
      type,
      value,
      reset,
      onChange
    }
  }

  const AnecdoteList = ({ anecdotes }) => (
    <div>
      <h2>Anecdotes</h2>
      <ul>
        {anecdotes.map(anecdote => 
        <li key={anecdote.id} >
        <Link to={`/anecdotes/${anecdote.id}`}>{anecdote.content}</Link>
        </li>
        )}
      </ul>
    </div>
  )

  const Notification = ( { anecdote } ) => {
      console.log('anecdootti', anecdote.value)
      if (anecdote !== '')
      return (
          <div>
              <p>you added blog {anecdote}</p>
          </div>
      )
    else return ''
  }



  const Anecdote = ( { anecdote } ) => {  
    return (
      <div>
          <h2>{anecdote.content}</h2>
          <p>has {anecdote.votes} votes</p>
          <p>more info from the link: <a href={anecdote.info}>{anecdote.info}</a></p>
      </div>
      )
  }

  const About = () => (
    <div>
      <h2>About anecdote app</h2>
      <p>According to Wikipedia:</p>

      <em>An anecdote is a brief, revealing account of an individual person or an incident.
        Occasionally humorous, anecdotes differ from jokes because their primary purpose is not simply to provoke laughter but to reveal a truth more general than the brief tale itself,
        such as to characterize a person by delineating a specific quirk or trait, to communicate an abstract idea about a person, place, or thing through the concrete details of a short narrative.
        An anecdote is "a story with a point."</em>

      <p>Software engineering is full of excellent anecdotes, at this app you can find the best and add more.</p>
    </div>
  )

  const Footer = () => (
    <div>
      Anecdote app for <a href='https://courses.helsinki.fi/fi/tkt21009'>Full Stack -websovelluskehitys</a>.

      See <a href='https://github.com/fullstack-hy2020/routed-anecdotes/blob/master/src/App.js'>https://github.com/fullstack-hy2019/routed-anecdotes/blob/master/src/App.js</a> for the source code.
    </div>
  )

  const CreateNew = (props) => {
    const content = useField('text')
    const author = useField('author')
    const info = useField('info')


    console.log('createn info', info)
    const history = useHistory()

    const handleSubmit = (e) => {
      e.preventDefault()
      console.log('content', content.value)
      props.addNew({
        content: content.value,
        author: author.value,
        info: info.value,
        votes: 0
      })
      history.push('/')
    }

    const handleReset = () => {
      return {
        content: content.reset}
    }

    return (
      <div>
        <h2>create a new anecdote</h2>
        <form id="form" onSubmit={handleSubmit}>
          <div>
            content
            <input {...content} />
          </div>
          <div>
            author
            <input {...author} />
          </div>
          <div>
            url for more info
            <input {...info} />
          </div>
          <button>create</button>
        </form>
        <button onClick={handleReset()}>reset</button>
      </div>
    )

  }

  const App = () => {
    const [anecdotes, setAnecdotes] = useState([
      {
        content: 'If it hurts, do it more often',
        author: 'Jez Humble',
        info: 'https://martinfowler.com/bliki/FrequencyReducesDifficulty.html',
        votes: 0,
        id: '1'
      },
      {
        content: 'Premature optimization is the root of all evil',
        author: 'Donald Knuth',
        info: 'http://wiki.c2.com/?PrematureOptimization',
        votes: 0,
        id: '2'
      }
    ])

    const [notification, setNotification] = useState('')

    const addNew = (anecdote) => {
      anecdote.id = (Math.random() * 10000).toFixed(0)
      setAnecdotes(anecdotes.concat(anecdote))
      setNotification(anecdote.content)
      setTimeout(() => {
        setNotification('')
      }, 10000);
    }

    const anecdoteById = (id) =>
      anecdotes.find(a => a.id === id)

    const vote = (id) => {
      const anecdote = anecdoteById(id)

      const voted = {
        ...anecdote,
        votes: anecdote.votes + 1
      }

      setAnecdotes(anecdotes.map(a => a.id === id ? voted : a))
    }

    const padding = {
        paddingRight: 5
      }

    const match = useRouteMatch('/anecdotes/:id')

    const anecdote = match 
    ? anecdotes.find(anecdote => anecdote.id === (match.params.id))
    : null


    return (
      <div class="container">
        <h1>Software anecdotes</h1>
        <div>
        <Link style={padding} to='/'>anecdotes</Link>
        <Link style={padding} to ='/create'>create new</Link>
        <Link style={padding} to ='/about'>about</Link>
      </div>

      <Notification anecdote={notification} />

      <Switch>
        <Route path='/anecdotes/:id'>
            <Anecdote anecdote={anecdote} /> 
        </Route>
        <Route path='/create'>
          <CreateNew addNew={addNew}/>
        </Route>
        <Route path='/about'>
            <About />
        </Route>
        <Route path='/'>
            <AnecdoteList anecdotes={anecdotes} />
        </Route>
      </Switch>
        <Footer />
      </div>
    )
  }


ReactDOM.render(<Router>
                    <App />
                    </Router>, document.getElementById('root'))
