import React, { useState, useEffect } from 'react'
import { useMutation, useQuery } from '@apollo/client'
import { EDIT_AUTHOR } from '../queries'
import { ALL_AUTHORS } from '../queries'
import Select from 'react-select';

const Authors = ( { show, authors } ) => {

  const [name, setName] = useState(authors.allAuthors.map(a => a.name))
  const [born, setBorn] = useState('')
  const [ editAuthor ] = useMutation(EDIT_AUTHOR, {
    refetchQueries: [ { query: ALL_AUTHORS } ]
  })


  console.log('frontin authit data', name)

  const submit = async (event) => {
    event.preventDefault()
    setName(event.target.value)
    console.log('working', event.target)
    editAuthor({  variables: { name, born } })

    setName('')
    setBorn('')
  }

  if (!show) {
    return null
  }

  const handleChange = (e) => {
    return name[e.target.value]
    }



  return (
    <div>
      <h2>authors</h2>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              born
            </th>
            <th>
              books
            </th>
          </tr>
          {console.log('frontin authorit', authors)}
          {authors.allAuthors.map(a =>
            <tr key={a.name}>
              <td>{a.name}</td>
              <td>{a.born}</td>
              <td>{a.bookCount}</td>
            </tr>
          )}
        </tbody>
      </table>

      <div>
      <h3>Set birthyear</h3>
        <form onSubmit={submit} >

            <select onChange={a => handleChange(a)} >
            {name.map(a => 
            <option value={a} key={a}>{a}</option>
            )}
            </select>
              <div>
              Born
            <input
              type='number'
              value={born}
              onChange={({ target }) => setBorn(parseInt(target.value))} 
              />
              </div>
              <button type='submit'>update author</button>
        </form>
      </div>

    </div>
  )
}

export default Authors
