import { gql } from '@apollo/client'

export const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      born
      bookCount
    }
  }
`

export const ALL_BOOKS = gql`
  query {
      allBooks {
          title
          author
          published
      }
  }
`

export const CREATE_BOOK = gql`
  mutation createBook($title: String!, $author: String!, $published: Int, $genres: [String!]!, $id: [Number]) {
    addBook(
      title: $title,
      author: $author,
      published: $published,
      genres: $genres,
      id: $id
    ) {
      title
      author
      published  
      genres
    }
  }
`

export const EDIT_AUTHOR = gql`
  mutation editAuthor($name: String!, $born: Int) {
    editAuthor(name: $name, setBornTo: $born)  {
      name
      born
  }
}
`
